import React from 'react'
import backTrack from '../utils/backTrack'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import {changeGrid} from '../redux/action'

import Button from '../styles/Button'
function multiAction(actions) {
    return (dispatch) => actions.map(a => dispatch(a));
  }
const Backtrack = ()=> {
    const lists = [{row:0,col:0, val:1},{row:0, col:1,val:2}]
    const actionLists = lists.map(a=> changeGrid(a))
    dispatch  = useDispatch()
    const myListDispatch = multiAction(actionLists)
    return(<Button onClick={() => myListDispatch(dispatch)} > Solve </Button>)
}

export default Backtrack;
