import React from 'react'
import backTrack from '../utils/backTrack'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import {changeGrid} from '../redux/action'

import Button from '../styles/Button'
function myListDispatch(actions,dispatch) {
    for(let i=0 ; i< actions.length;i++)
    {    console.log("dispatching")
          setTimeout(() =>  dispatch(actions[i]), 100000)
         
    }
  }
const Backtrack = ()=> {
    const lists = [{row:0,col:0, val:1},{row:0, col:1,val:2}]
    const actionLists = lists.map(a=> changeGrid(a))
    const dispatch  = useDispatch()
    return(<Button onClick={() => myListDispatch(actionLists,dispatch)} > Solve </Button>)
}

export default Backtrack;
