import React,{useState} from 'react'
import BlockContainer from '../styles/BlockContainer'
import { useSelector } from 'react-redux'

const Block = ({ colIndex, rowIndex }) => {
  const [isInputActive, setIsInputActive] = useState(false)
  const val = useSelector(state=> state.grid? state.grid[colIndex][rowIndex]:0)
  return isInputActive?<BlockContainer> <input size="50"/> </BlockContainer>:<BlockContainer onClick= {()=>setIsInputActive(!isInputActive)}>{val}</BlockContainer>
}

export default Block
