import createNewGrid from '../utils/createNewGrid.js'
function reducer( state={}, action)
{
    switch(action.type)
    {
        case "CREATE_GRID" : return{ ...state, grid: createNewGrid(),}
        case "CHANGE_GRID": var gridNew = [...state.grid]
                                gridNew[action.payload.row][action.payload.col] = action.payload.val
                            return {...state, grid: gridNew}    
        default:
            return state
    }
} 

export default reducer;
