import React,{useState} from 'react'
import BlockContainer from '../styles/BlockContainer'
import Input from '../styles/InputContainer'
import { useSelector } from 'react-redux'

const Block = ({ colIndex, rowIndex }) => {
  const [isInputActive, setIsInputActive] = useState(false)
  const [inputValue, setInputValue] = useState("")
  const val = useSelector(state=> state.grid? state.grid[colIndex][rowIndex]:0)
  const enter = useKeypress('Enter');
  const esc = useKeypress('Escape');
  useEffect(() => {

    if (isInputActive) {

      // if Enter is pressed, save the text and case the editor

      if (enter) {

        

        setIsInputActive(false);

      }

      // if Escape is pressed, revert the text and close the editor

      if (esc) {

        

        setIsInputActive(false);

      }

    }

  }, [enter, esc]);
  return isInputActive?<Input type='text' value={inputValue}

  onChange={(e) => setInputValue(e.target.value)}/>:<BlockContainer onClick= {()=>setIsInputActive(!isInputActive)}>{val}</BlockContainer>
}

export default Block
