import styled, { css } from 'styled-components'

const Input = styled.input`
${({ theme }) => css`
background-color: ${theme.colors.white};
border: solid 1px ${theme.colors.black};
display: flex;
flex-grow: 1;
flex-shrink: 0;
flex-basis: 0;
font-size: 20px;
font-weight: bold;
height: auto;
transition: ${theme.transition};
user-select: no
`}
`
export default Input;
