import styled, { css } from 'styled-components'

const Row = styled.div`
  ${({ theme }) => css`
    display: flex;
    flex-flow: row;

    &:nth-child(1) {
      div {
        border-top: solid 4px ${theme.colors.blue};
      }
    }
    &:nth-child(3),
    &:nth-child(6) {
      div {
        border-bottom: solid 3px ${theme.colors.blue};
      }
    }
    &:nth-child(9) {
      border-bottom: solid 4px ${theme.colors.blue};
    }

    div {
      &:nth-child(1) {
        border-left: solid 4px ${theme.colors.blue};
      }
      &:nth-child(3),
      &:nth-child(6),
      &:nth-child(9) {
        border-right: solid 4px ${theme.colors.blue};
      }
      &:nth-child(4),
      &:nth-child(7) {
        border-left: none;
      }
    }
  `}`

  export default Row;
