import styled, { css } from 'styled-components'

const Input = styled.input`
${({ theme }) => css`
background-color: ${theme.colors.white};
border: solid 1px ${theme.colors.black};
width: 50px;
height: 50px;
transition: ${theme.transition};
user-select: no
`}
`
export default Input;
